.body {
	@apply text-gray-700 text-lg; 
	ul {
    	@apply grid gap-6 mt-4 pl-6;
    } 
	li:before {
    	content: '';
        background-image: url('data:image/svg+xml;base64,');
		@apply bg-contain bg-no-repeat block w-5 h-5 transform -translate-x-7 translate-y-7;
    }
}

.image-grid {
  @apply grid gap-4;
  grid-template-columns: minmax(10px, 50px) auto auto minmax(10px, 50px);
  grid-template-rows: minmax(25px, 100px) auto auto minmax(10px, 50px);

  img {
    @apply rounded-lg h-full w-full object-cover;
  }

  .bottom-right {
    grid-row-start: 3;
    grid-row-end: 5;
    grid-column-start: 3;
  }
}

